import React from 'react';
import { useTranslation } from 'react-i18next';

const About: React.FC = () => {
  const { t } = useTranslation();

  const contactInfo = [
    {
      icon: 'üìç',
      label: t('about.location'),
      value: 'Ciudad, Pa√≠s'
    },
    {
      icon: '‚úâÔ∏è',
      label: t('about.email'),
      value: 'sebastian@email.com'
    },
    {
      icon: 'üì±',
      label: t('about.phone'),
      value: '+1 (555) 123-4567'
    }
  ];

  return (
    <section id="about" className="section">
      <div className="animate-fade-in-up">
        <h2 className="section-title font-cinzel">
          {t('about.title')}
        </h2>

        <div className="grid grid-2" style={{ alignItems: 'center' }}>
          <div className="animate-fade-in-left">
            <div className="card">
              <p style={{ fontSize: '1.2rem', lineHeight: '1.8', marginBottom: '2rem' }}>
                {t('about.description')}
              </p>

              <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
                {contactInfo.map((info, index) => (
                  <div 
                    key={index}
                    style={{ 
                      display: 'flex', 
                      alignItems: 'center', 
                      gap: '1rem',
                      padding: '0.5rem 0',
                      borderBottom: '1px solid var(--border-color)'
                    }}
                  >
                    <span style={{ fontSize: '1.5rem' }}>{info.icon}</span>
                    <div>
                      <strong style={{ color: 'var(--accent-primary)' }}>
                        {info.label}:
                      </strong>
                      <span style={{ marginLeft: '0.5rem', color: 'var(--text-secondary)' }}>
                        {info.value}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="animate-fade-in-right">
            <div className="card" style={{ textAlign: 'center' }}>
              {/* Placeholder para imagen de perfil */}
              <div 
                style={{
                  width: '250px',
                  height: '250px',
                  borderRadius: '50%',
                  background: 'var(--gradient-accent)',
                  margin: '0 auto 2rem',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '4rem',
                  color: 'var(--bg-primary)',
                  border: '4px solid var(--accent-primary)',
                  boxShadow: '0 10px 30px var(--shadow-accent)'
                }}
              >
                üë®‚Äçüíª
              </div>
              
              <h3 className="font-playfair" style={{ 
                fontSize: '1.5rem', 
                color: 'var(--accent-primary)',
                marginBottom: '1rem'
              }}>
                Sebastian Rivera
              </h3>
              
              <p style={{ 
                color: 'var(--text-secondary)',
                fontStyle: 'italic'
              }}>
                "La excelencia no es una habilidad, es una actitud."
              </p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
