import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';

const Contact: React.FC = () => {
  const { t } = useTranslation();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Aquí iría la lógica para enviar el formulario
    console.log('Formulario enviado:', formData);
    alert('¡Mensaje enviado! Te contactaré pronto.');
    setFormData({ name: '', email: '', message: '' });
  };

  const socialLinks = [
    {
      name: 'LinkedIn',
      icon: '💼',
      url: 'https://linkedin.com/in/sebastian-rivera',
      color: '#0077b5'
    },
    {
      name: 'GitHub',
      icon: '💻',
      url: 'https://github.com/sebastian-rivera',
      color: '#333'
    },
    {
      name: 'Twitter',
      icon: '🐦',
      url: 'https://twitter.com/sebastian_dev',
      color: '#1da1f2'
    },
    {
      name: 'Email',
      icon: '✉️',
      url: 'mailto:sebastian@email.com',
      color: '#ea4335'
    }
  ];

  return (
    <section id="contact" className="section">
      <div className="animate-fade-in-up">
        <h2 className="section-title font-cinzel">
          {t('contact.title')}
        </h2>

        <div className="grid grid-2" style={{ alignItems: 'start' }}>
          {/* Información de contacto */}
          <div className="animate-fade-in-left">
            <div className="card">
              <h3 className="font-playfair" style={{
                color: 'var(--accent-primary)',
                fontSize: '1.5rem',
                marginBottom: '1rem'
              }}>
                Hablemos
              </h3>
              
              <p style={{
                marginBottom: '2rem',
                lineHeight: '1.6',
                color: 'var(--text-secondary)'
              }}>
                {t('contact.description')}
              </p>

              {/* Redes sociales */}
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(2, 1fr)',
                gap: '1rem',
                marginBottom: '2rem'
              }}>
                {socialLinks.map((link, index) => (
                  <a
                    key={index}
                    href={link.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{
                      display: 'flex',
                      alignItems: 'center',
                      padding: '1rem',
                      background: 'var(--bg-tertiary)',
                      borderRadius: '8px',
                      textDecoration: 'none',
                      color: 'var(--text-primary)',
                      border: '1px solid var(--border-color)',
                      transition: 'all 0.3s ease'
                    }}
                    onMouseOver={(e) => {
                      e.currentTarget.style.borderColor = 'var(--accent-primary)';
                      e.currentTarget.style.transform = 'translateY(-2px)';
                    }}
                    onMouseOut={(e) => {
                      e.currentTarget.style.borderColor = 'var(--border-color)';
                      e.currentTarget.style.transform = 'translateY(0)';
                    }}
                  >
                    <span style={{ fontSize: '1.5rem', marginRight: '0.75rem' }}>
                      {link.icon}
                    </span>
                    <span style={{ fontWeight: '600' }}>
                      {link.name}
                    </span>
                  </a>
                ))}
              </div>

              {/* Información adicional */}
              <div style={{
                padding: '1.5rem',
                background: 'var(--gradient-accent)',
                borderRadius: '8px',
                color: 'var(--bg-primary)',
                textAlign: 'center'
              }}>
                <p style={{ fontWeight: '600', marginBottom: '0.5rem' }}>
                  ¿Prefieres una llamada?
                </p>
                <p>📞 +1 (555) 123-4567</p>
              </div>
            </div>
          </div>

          {/* Formulario de contacto */}
          <div className="animate-fade-in-right">
            <div className="card">
              <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '1.5rem' }}>
                  <label style={{
                    display: 'block',
                    marginBottom: '0.5rem',
                    fontWeight: '600',
                    color: 'var(--accent-primary)'
                  }}>
                    {t('contact.form.name')}
                  </label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    style={{
                      width: '100%',
                      padding: '0.75rem',
                      border: '1px solid var(--border-color)',
                      borderRadius: '8px',
                      background: 'var(--bg-tertiary)',
                      color: 'var(--text-primary)',
                      fontSize: '1rem',
                      transition: 'border-color 0.3s ease'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = 'var(--accent-primary)';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'var(--border-color)';
                    }}
                  />
                </div>

                <div style={{ marginBottom: '1.5rem' }}>
                  <label style={{
                    display: 'block',
                    marginBottom: '0.5rem',
                    fontWeight: '600',
                    color: 'var(--accent-primary)'
                  }}>
                    {t('contact.form.email')}
                  </label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                    style={{
                      width: '100%',
                      padding: '0.75rem',
                      border: '1px solid var(--border-color)',
                      borderRadius: '8px',
                      background: 'var(--bg-tertiary)',
                      color: 'var(--text-primary)',
                      fontSize: '1rem',
                      transition: 'border-color 0.3s ease'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = 'var(--accent-primary)';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'var(--border-color)';
                    }}
                  />
                </div>

                <div style={{ marginBottom: '1.5rem' }}>
                  <label style={{
                    display: 'block',
                    marginBottom: '0.5rem',
                    fontWeight: '600',
                    color: 'var(--accent-primary)'
                  }}>
                    {t('contact.form.message')}
                  </label>
                  <textarea
                    name="message"
                    value={formData.message}
                    onChange={handleInputChange}
                    required
                    rows={5}
                    style={{
                      width: '100%',
                      padding: '0.75rem',
                      border: '1px solid var(--border-color)',
                      borderRadius: '8px',
                      background: 'var(--bg-tertiary)',
                      color: 'var(--text-primary)',
                      fontSize: '1rem',
                      resize: 'vertical',
                      transition: 'border-color 0.3s ease'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = 'var(--accent-primary)';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = 'var(--border-color)';
                    }}
                  />
                </div>

                <button
                  type="submit"
                  className="btn"
                  style={{
                    width: '100%',
                    fontSize: '1.1rem'
                  }}
                >
                  {t('contact.form.send')}
                </button>
              </form>
            </div>
          </div>
        </div>

        {/* Referencias Profesionales */}
        <div className="animate-fade-in-up" style={{ marginTop: '4rem' }}>
          <h3 className="section-subtitle font-cinzel" style={{ 
            textAlign: 'center', 
            marginBottom: '3rem',
            color: 'var(--accent-color)'
          }}>
            {t('contact.references.title')}
          </h3>

          <div className="grid grid-3">
            {Object.entries(t('contact.references.contacts', { returnObjects: true }) as Record<string, any>).map(([key, ref]) => (
              <div 
                key={key}
                className="card animate-fade-in-up"
                style={{
                  background: 'var(--card-bg)',
                  border: '1px solid var(--border-color)',
                  borderRadius: '12px',
                  padding: '2rem',
                  textAlign: 'center',
                  transition: 'all 0.3s ease'
                }}
              >
                <div style={{ 
                  fontSize: '3rem', 
                  marginBottom: '1rem',
                  color: 'var(--accent-color)'
                }}>
                  👨‍💼
                </div>
                
                <h4 style={{
                  fontSize: '1.2rem',
                  marginBottom: '0.5rem',
                  color: 'var(--accent-color)',
                  fontFamily: 'var(--font-heading)'
                }}>
                  {ref.name}
                </h4>
                
                <p style={{
                  fontSize: '0.95rem',
                  marginBottom: '1.5rem',
                  color: 'var(--text-secondary)',
                  fontStyle: 'italic'
                }}>
                  {ref.title}
                </p>
                
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  gap: '0.5rem'
                }}>
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    justifyContent: 'center',
                    gap: '0.5rem',
                    color: 'var(--text-color)'
                  }}>
                    <span>📞</span>
                    <span style={{ fontSize: '0.9rem' }}>{ref.phone}</span>
                  </div>
                  
                  <div style={{ 
                    display: 'flex', 
                    alignItems: 'center', 
                    justifyContent: 'center',
                    gap: '0.5rem',
                    color: 'var(--text-color)'
                  }}>
                    <span>✉️</span>
                    <span style={{ fontSize: '0.9rem' }}>{ref.email}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
